function toMinutes(t) {
    var [hour, minutePart] = t.trim().split(":");
    var minute = parseInt(minutePart, 10);
    var ampm = t.toUpperCase().includes("PM") ? 12 : 0;
    return (parseInt(hour, 10) % 12 + ampm) * 60 + minute;
}

